import React, { useState, useEffect } from 'react';
import { Link, useHistory } from 'react-router-dom'; // Import de useHistory depuis 'react-router-dom'
import '../styles/ChantierBox.css';

const getTypeLabel = (typeNum) => {
  switch (typeNum) {
    case 0:
      return "Climatisation";
    case 1:
      return "Pompe à chaleur";
    case 2:
      return "Chaudière";
    default:
      return "Unknown Type";
  }
};

const ChantierBox = ({ chantierId }) => {
  const [chantier, setChantier] = useState(null);
  const history = useHistory(); // Initialisation de useHistory

  useEffect(() => {
    fetch(`http://localhost:5257/api/chantier/${chantierId}`)
      .then(response => response.json())
      .then(data => setChantier(data))
      .catch(error => console.error('Error fetching chantier details:', error));
  }, [chantierId]);

  if (!chantier) {
    return <div>No data available</div>;
  }

  // Fonction pour naviguer vers les détails du chantier avec la provenance 'planninga'
  const navigateToChantierDetails = () => {
    history.push({
      pathname: `/chantier/${chantier.chantierId}`,
      state: { from: 'planninga' }
    });
  };

  return (
    <div className='boxchantier'>
      <h3>{getTypeLabel(chantier.type)}</h3>
      <p>Client: {chantier.client}</p>
      <p>Date de début: {chantier.dateDebut}</p>
      <p>Adresse: {chantier.adresse}</p>
      <button onClick={navigateToChantierDetails}>Voir les détails</button>
    </div>
  );
};

export default ChantierBox;
