import React, { useState, useEffect } from 'react';
import BottomNavbar from '../components/Navbar';
import Header from '../components/Header';
import ChantierBox from '../components/ChantierBox';
import '../styles/AccueilO.css';

const AccueilO = () => {
  const [chantierIds, setChantierIds] = useState([]);

  useEffect(() => {
    fetch('http://localhost:5257/api/chantier')
      .then(response => response.json())
      .then(data => {
        // Filtrer les chantiers du jour pour récupérer seulement les IDs
        const ids = data
          .filter(chantier => {
            const today = new Date().toISOString().split('T')[0];
            const chantierDate = new Date(chantier.dateDebut).toISOString().split('T')[0];
            return today === chantierDate;
          })
          .map(chantier => chantier.chantierId);
        setChantierIds(ids);
      })
      .catch(error => console.error('Error fetching chantiers:', error));
  }, []);

  return (
    <div>
      <Header titre="Accueil" />
      <div className="ouvr">
        <h3>Mon programme du jour</h3>
        {chantierIds.length > 0 ? (
          <div className="chantiers">
            <ul>
              {chantierIds.map(id => (
                <li key={id}>
                  <ChantierBox chantierId={id} />
                </li>
              ))}
            </ul>
          </div>
        ) : (
          <p>Aucun chantier programmé pour aujourd'hui.</p>
        )}
      </div>
      <BottomNavbar />
    </div>
  );
};

export default AccueilO;
