import React, { useState, useEffect } from 'react';

const ChantierBox = ({ chantierId }) => {
  const [chantier, setChantier] = useState(null);

  useEffect(() => {
    fetch(`http://localhost:5257/api/chantier/${chantierId}`)
      .then(response => response.json())
      .then(data => setChantier(data))
      .catch(error => console.error('Error fetching chantier details:', error));
  }, [chantierId]);

  return (
    <div className='bo'>
      {chantier ? (
        <div>
          <h4>Chantier ID: {chantier.ChantierId}</h4>
          <p>Type: {chantier.Type}</p>
          <p>Description: {chantier.Description}</p>
          <p>Date de début: {chantier.DateDebut}</p>
          <p>Date de fin: {chantier.DateFin}</p>
          <p>Statut: {chantier.Statut}</p>
          <p>Adresse: {chantier.Adresse}</p>
          {/* Autres détails du chantier */}
        </div>
      ) : (
        <p>Chargement des détails du chantier...</p>
      )}
    </div>
  );
};

export default ChantierBox;
