import React, { useState } from 'react';
import '../styles/Login.css';
import axios from 'axios';
import  '../styles/Global.css'; // Importez le style global
import Navordi from '../components/Navordi';


const Chantier = () => {
    const [type, setType] = useState(0);
    const [description, setDescription] = useState('');
    const [dateDebut, setDateD] = useState();
    const [dateFin, setDateF]= useState();
    const [adresse, setAdresse]= useState();
    const [client, setClient] = useState();

    const types = ['CLIMATISATION', 'POMPE', 'CHAUDIERE'];
    const [ajoutOk, setAjout]=useState(false);
    const handleDateChange1 = (event) => {
        setDateD(event.target.value);
      };

    const ajoutChantier = async () => {
        try {
            const response = await axios.post ('http://localhost:5257/api/chantier/', {
                Type: type,
                Description: description,
                DateDebut: dateDebut,
                DateFin: dateFin,
                Adresse: adresse,
                Client: client,
            });

            if (response && response.data) {
                console.log('Reponse du serveur: ', response.data);
                setAjout(true);
            } else {
                console.error('Reponse du serveur non valide:', response);
            }
        } catch (error) {
            console.error('Erreur lors de la requête:', error.response ? error.response.data : error.message);
        }
    }; 

    return (
        <div className='login'>
            <Navordi></Navordi>
            <h1>Ajout d'un chantier</h1>
            <form>
                <select value={type} onChange={(e) => setType(e.target.value)}>
                    <option value="">Sélectionnez le type de chantier</option>
                    {types.map((r) => (
                        <option key={r} value={r}>
                            {r}
                        </option>
                    ))}
                </select>
                <input 
                    type="text"
                    placeholder="Entrez le nom du client"
                    value ={client}
                    onChange={(e) => setClient(e.target.value)}
                />
                <input 
                    type="text"
                    placeholder="Entrez l'adresse"
                    value ={adresse}
                    onChange={(e) => setAdresse(e.target.value)}
                />
                <input 
                    type="text"
                    placeholder="Description"
                    value ={description}
                    onChange={(e) => setDescription(e.target.value)}
                />
                <div className="date-selector">
                    <label htmlFor="date">Sélectionnez une date :</label>
                    <input
                        type="date"
                        id="date"
                        name="date"
                        value={dateDebut}
                        onChange={handleDateChange}
                    />
        </div>

            </form>
        </div>
    );

}

export default Chantier;
