import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom'; 
import '../styles/ChantierBox.css';

const ChantierBox = ({ chantier }) => {
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    setLoading(true);
    // Effectuer une requête pour récupérer les données du chantier
    fetch(`http://localhost:5257/api/chantier/${chantier.ChantierId}`)
      .then(response => response.json())
      .then(data => {
        setChantier(data);
        setLoading(false);
      })
      .catch(error => {
        console.error('Error fetching chantier:', error);
        setLoading(false);
      });
  }, [chantier.ChantierId]);

  if (loading) {
    return <div>Loading...</div>;
  }

  return (
    <div className='boxchantier'>
      <h3>{chantier.NomChantier}</h3>
      <p>Description: {chantier.Description}</p>
      <p>Date de début: {chantier.DateDebut}</p>
      <p>Adresse: {chantier.Adresse}</p>
      <Link to={`/chantier/${chantier.ChantierId}`}>
        <button>Voir les détails</button>
      </Link>
    </div>
  );
};

export default ChantierBox;
