import React, { useState, useEffect } from 'react'; 
import BottomNavbar from '../components/Navbar';
import Header from '../components/Header';
import '../styles/AccueilO.css';
import { useParams } from 'react-router-dom';


const ChantierDetails = () => {
  const [chantier, setChantier] = useState(null); 
  const { chantierId } = useParams();
  console.log(chan)


  useEffect(() => {
    // Effectuer une requête pour récupérer les détails du chantier
    fetch(`http://localhost:5257/api/chantier/${chantierId}`)
      .then(response => response.json())
      .then(data => setChantier(data)) // Mettre à jour l'état avec les détails du chantier
      .catch(error => console.error('Error fetching chantier details:', error));
  }, [chantierId]); // Déclencher l'effet à chaque changement de chantierId

  if (!chantier) {
    return <div>No data available</div>; // Afficher un message si aucun chantier n'est disponible
  }

  return (
    <div>
      <Header titre="Détails Chantier" />
      {/* Afficher les détails du chantier ici */}
      <div>
        <p>Nom du chantier : {chantier.nom}</p>
        <p>Description : {chantier.description}</p>
        <p>Date de début : {chantier.dateDebut}</p>
        {/* Ajouter d'autres détails du chantier ici */}
      </div>
      <BottomNavbar />
    </div>
  );
};

export default ChantierDetails;
