import React, { useState } from 'react';
import '../styles/Login.css';
import axios from 'axios';
import Navordi from '../components/Navordi';
import DateTimePicker from 'react-datetime-picker';

const Chantier = () => {
    const [type, setType] = useState(0);
    const [description, setDescription] = useState('');
    const [dateDebut, setDateD] = useState(new Date()); // Définir la date et l'heure de début par défaut
    const [dateFin, setDateF] = useState(new Date()); // Définir la date et l'heure de fin par défaut
    const [adresse, setAdresse] = useState('');
    const [client, setClient] = useState('');
    const types = ['CLIMATISATION', 'POMPE', 'CHAUDIERE'];
    const [ajoutOk, setAjout] = useState(false);

    const ajoutChantier = async () => {
        try {
            const response = await axios.post ('http://localhost:5257/api/chantier/', {
                Type: type,
                Description: description,
                DateDebut: dateDebut,
                DateFin: dateFin,
                Adresse: adresse,
                Client: client,
            });

            if (response && response.data) {
                console.log('Réponse du serveur: ', response.data);
                setAjout(true);
            } else {
                console.error('Réponse du serveur non valide:', response);
            }
        } catch (error) {
            console.error('Erreur lors de la requête:', error.response ? error.response.data : error.message);
        }
    };

    return (
        <div className='login'>
            <Navordi />
            <h1>Ajout d'un chantier</h1>
            <form>
                <select value={type} onChange={(e) => setType(e.target.value)}>
                    <option value="">Sélectionnez le type de chantier</option>
                    {types.map((r) => (
                        <option key={r} value={r}>
                            {r}
                        </option>
                    ))}
                </select>
                <input 
                    type="text"
                    placeholder="Entrez le nom du client"
                    value={client}
                    onChange={(e) => setClient(e.target.value)}
                />
                <input 
                    type="text"
                    placeholder="Entrez l'adresse"
                    value={adresse}
                    onChange={(e) => setAdresse(e.target.value)}
                />
                <input 
                    type="text"
                    placeholder="Description"
                    value={description}
                    onChange={(e) => setDescription(e.target.value)}
                />
                <div className="date-selector">
                    <label>Date et heure de début :</label>
                    <DateTimePicker
                        onChange={setDateD}
                        value={dateDebut}
                    />
                </div>
                <div className="date-selector">
                    <label>Date et heure de fin :</label>
                    <DateTimePicker
                        onChange={setDateF}
                        value={dateFin}
                    />
                </div>
                <button type="button" onClick={ajoutChantier}>Ajouter</button>
            </form>
        </div>
    );
}

export default Chantier;
