import React, { useState, useEffect } from 'react';
import { MapContainer, TileLayer, Marker, Popup } from 'react-leaflet';

const LeafletMap = ({ address }) => {
  const [coordinates, setCoordinates] = useState([0, 0]);

  useEffect(() => {
    const fetchCoordinates = async () => {
      try {
        // Formater l'adresse pour l'utiliser dans la requête
        const formattedAddress = address.replace(/\s/g, '+');

        // Envoyer une requête au service de géocodage
        const response = await fetch(`https://nominatim.openstreetmap.org/search?format=json&q=${encodeURIComponent(formattedAddress)}`);
        const data = await response.json();

        // Analyser la réponse pour extraire les coordonnées géographiques
        if (data && data.length > 0) {
          setCoordinates([parseFloat(data[0].lat), parseFloat(data[0].lon)]);
          console.log(coordinates)
        }
      } catch (error) {
        console.error('Error fetching coordinates:', error);
      }
    };

    // Appeler la fonction pour récupérer les coordonnées lorsque l'adresse change
    fetchCoordinates();
  }, [address]);

  return (
    <MapContainer center={coordinates} zoom={13}>
      <TileLayer url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png" />
      <Marker position={coordinates}>
        <Popup>{address}</Popup>
      </Marker>
    </MapContainer>
  );
};

export default LeafletMap;
