import React, { useState, useEffect } from 'react'; 
import { useHistory, useParams } from 'react-router-dom'; 
import BottomNavbar from '../components/Navbar';
import Header from '../components/Header';
import LeafletMap from '../components/Map';
import "../styles/detailchantier.css"
import Navordi from '../components/Navordi';

const getTypeLabel = (typeNum) => {
  switch (typeNum) {
    case 0:
      return "Climatisation";
    case 1:
      return "Pompe à chaleur";
    case 2:
      return "Chaudière";
    default:
      return "Unknown Type";
  }
};

const ChantierDetails = () => {
  const [chantier, setChantier] = useState(null); 
  const { chantierId } = useParams();
  const history = useHistory();
  const [previousLocation, setPreviousLocation] = useState(null);

  useEffect(() => {
    fetch(`http://localhost:5257/api/chantier/${chantierId}`)
      .then(response => response.json())
      .then(data => setChantier(data))
      .catch(error => console.error('Error fetching chantier details:', error));

    // Mise à jour de l'emplacement précédent à chaque changement d'emplacement
    return history.listen(location => {
      setPreviousLocation(location);
    });
  }, [chantierId, history]);

  if (!chantier) {
    return <div>No data available</div>;
  }

  // Vérifier si l'emplacement précédent est "planninga" ou "planning"
  const isFromPlanningA = previousLocation && previousLocation.state && previousLocation.state.from === "planninga";
  const isFromPlanning = previousLocation && previousLocation.state && previousLocation.state.from === "planning";

  return (
    <div>
      {isFromPlanningA && <Navordi />}
      <Header titre="Détails Chantier" />
      <div className="detailchantier">
        <h2>{getTypeLabel(chantier.type)}</h2>
        <p>Client : {chantier.client}</p>
        <p>Description : {chantier.description}</p>
        <p>Date de début : {chantier.dateDebut}</p>
        <p>Adresse : {chantier.adresse}</p>
        <LeafletMap address={chantier.adresse} />
      </div>
      {isFromPlanning && <BottomNavbar />}
    </div>
  );
};

export default ChantierDetails;
